const getBlocks = require('useref/lib/getBlocks');
const transformReferences = require('useref/lib/transformReferences');
const compactContent = require('useref/lib/compactContent');

module.exports = (content, options) => {
  const blocks = getBlocks(content);
  const opts = options || {};
  const transformedContent = transformReferences(blocks, content, opts);
  const replaced = compactContent(blocks, opts);

  return [ transformedContent, replaced ];
};
